import React, { useState } from "react";
import { register } from "./UserFunctions";

const Register = ({ history }) => {
  const [state, setState] = useState({
    first_name: "",
    last_name: "",
    email: "",
    password: "",
    errors: {},
  });

  const { first_name, last_name, email, password, errors } = state;

  const onChange = (e) => {
    setState({ ...state, [e.target.name]: e.target.value });
  };

  const onSubmit = (e) => {
    e.preventDefault();

    if (validateForm()) {
      const newUser = {
        first_name,
        last_name,
        email,
        password,
      };

      register(newUser)
        .then((res) => {
          history.push(`/login`);
        })
        .catch((error) => {
          console.error("Registration failed:", error);
        });
    }
  };

  const validateForm = () => {
    let isValid = true;
    const errors = {};

    if (first_name.trim() === "") {
      errors.first_name = "First name is required";
      isValid = false;
    }

    if (last_name.trim() === "") {
      errors.last_name = "Last name is required";
      isValid = false;
    }

    if (email.trim() === "") {
      errors.email = "Email address is required";
      isValid = false;
    } else if (!isValidEmail(email)) {
      errors.email = "Invalid email address";
      isValid = false;
    }

    if (password.trim() === "") {
      errors.password = "Password is required";
      isValid = false;
    } else if (!isValidPassword(password)) {
      errors.password =
        "Password must be at least 6 characters and contain at least 1 uppercase letter, 1 lowercase letter, 1 numeric digit, and 1 special character";
      isValid = false;
    }

    setState({ ...state, errors });
    return isValid;
  };

  const isValidEmail = (email) => {
    // Basic email format validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const isValidPassword = (password) => {
    // Password validation requirements
    const passwordRegex =
      /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[@$!%*?&])[A-Za-z\d@$!%*?&]{6,}$/;
    return passwordRegex.test(password);
  };

  const register = (user) => {
    // Your registration logic/API call here
    return fetch("/api/register", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(user),
    })
      .then((response) => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error("Registration failed");
        }
      })
      .catch((error) => {
        console.error(error);
        throw error;
      });
  };

  const isFormValid =
    first_name.trim() !== "" &&
    last_name.trim() !== "" &&
    email.trim() !== "" &&
    password.trim() !== "" &&
    isValidEmail(email) &&
    isValidPassword(password);

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-6 mt-5 mx-auto">
          <form noValidate onSubmit={onSubmit}>
            <h1 className="h3 mb-3 font-weight-normal">Register</h1>
            <div className="form-group">
              <label htmlFor="name">First name</label>
              <input
                type="text"
                className={`form-control ${errors.first_name && "is-invalid"}`}
                name="first_name"
                placeholder="Enter your first name"
                value={first_name}
                onChange={onChange}
              />
              {errors.first_name && (
                <div className="invalid-feedback">{errors.first_name}</div>
              )}
            </div>
            <div className="form-group">
              <label htmlFor="name">Last name</label>
              <input
                type="text"
                className={`form-control ${errors.last_name && "is-invalid"}`}
                name="last_name"
                placeholder="Enter your last name"
                value={last_name}
                onChange={onChange}
              />
              {errors.last_name && (
                <div className="invalid-feedback">{errors.last_name}</div>
              )}
            </div>
            <div className="form-group">
              <label htmlFor="email">Email address</label>
              <input
                type="email"
                className={`form-control ${errors.email && "is-invalid"}`}
                name="email"
                placeholder="Enter email"
                value={email}
                onChange={onChange}
              />
              {errors.email && (
                <div className="invalid-feedback">{errors.email}</div>
              )}
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                className={`form-control ${errors.password && "is-invalid"}`}
                name="password"
                placeholder="Password"
                value={password}
                onChange={onChange}
              />
              {errors.password && (
                <div className="invalid-feedback">{errors.password}</div>
              )}
            </div>
            <button
              type="submit"
              className="btn btn-lg btn-primary btn-block"
              disabled={!isFormValid}
            >
              Register!
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Register;
